CREATE TABLE EMPRESA (ID SERIAL NOT NULL PRIMARY KEY, CNPJ varchar(255) NOT NULL, RAZAO_SOCIAL varchar(255) NOT NULL, DATA_CRIACAO TIMESTAMP NOT NULL, DATA_ATUALIZACAO TIMESTAMP NOT NULL);
CREATE TABLE FUNCIONARIO (ID SERIAL NOT NULL PRIMARY KEY, NOME varchar(255) NOT NULL, EMAIL varchar(255) NOT NULL, SENHA varchar(255) NOT NULL , CPF varchar(255) NOT NULL, VALOR_HORA NUMERIC(2), QTD_HORAS_DIARIAS NUMERIC(2), QTD_HORAS_ALMOCO NUMERIC(2), PERFIL varchar(20), DATA_CRIACAO timestamp, DATA_ATUALIZACAO timestamp, EMPRESA_ID INTEGER REFERENCES EMPRESA(ID));
CREATE TABLE LANCAMENTO (ID SERIAL NOT NULL PRIMARY KEY, DATA TIMESTAMP NOT NULL, DESCRICAO varchar(255), LOCALIZACAO varchar(255), DATA_CRIACAO TIMESTAMP NOT NULL, DATA_ATUALIZACAO TIMESTAMP NOT NULL, TIPO_LANCAMENTO varchar(255) NOT NULL, FUNCIONARIO_ID INTEGER NOT NULL REFERENCES FUNCIONARIO(ID));